def climbStairs(n):
    # Versione ottimizzata O(1) spazio
    if n <= 2:
        return n

    prev2, prev1 = 1, 2
    for i in range(3, n + 1):
        current = prev1 + prev2
        prev2, prev1 = prev1, current

    return prev1

def fib(n):
    # Versione O(1) spazio
    if n <= 1:
        return n

    a, b = 0, 1
    for _ in range(2, n + 1):
        a, b = b, a + b

    return b

def minCostClimbingStairs(cost):
    # Versione O(1) spazio
    prev2 = cost[0]
    prev1 = cost[1]

    for i in range(2, len(cost)):
        current = cost[i] + min(prev1, prev2)
        prev2, prev1 = prev1, current

    return min(prev1, prev2)

