def maxSubarraySumCircular(nums):
    def kadane_max(arr):
        current = maximum = arr[0]
        for i in range(1, len(arr)):
            current = max(arr[i], current + arr[i])
            maximum = max(maximum, current)
        return maximum

    def kadane_min(arr):
        current = minimum = arr[0]
        for i in range(1, len(arr)):
            current = min(arr[i], current + arr[i])
            minimum = min(minimum, current)
        return minimum

    # Caso 1: subarray normale
    max_kadane = kadane_max(nums)

    # Caso 2: subarray circolare = total - min_subarray
    total_sum = sum(nums)
    min_kadane = kadane_min(nums)
    max_circular = total_sum - min_kadane

    # Caso speciale: tutti elementi negativi
    if max_circular == 0:
        return max_kadane

    return max(max_kadane, max_circular)

def canPartition(nums):
    total_sum = sum(nums)

    # Se dispari, impossibile partizionare equamente
    if total_sum % 2 != 0:
        return False

    target = total_sum // 2
    n = len(nums)

    # dp[i][j] = possiamo ottenere somma j usando primi i elementi
    dp = [[False] * (target + 1) for _ in range(n + 1)]

    # Caso base: somma 0 sempre possibile (non prendere nulla)
    for i in range(n + 1):
        dp[i][0] = True

    for i in range(1, n + 1):
        for j in range(1, target + 1):
            # Non prendere l'elemento i-1
            dp[i][j] = dp[i-1][j]

            # Prendere l'elemento i-1 (se possibile)
            if nums[i-1] <= j:
                dp[i][j] = dp[i][j] or dp[i-1][j - nums[i-1]]

    return dp[n][target]

