    if total_sum % 2 != 0:
        return False

    target = total_sum // 2

    # Versione 1D ottimizzata
    dp = [False] * (target + 1)
    dp[0] = True

    for num in nums:
        # Itera all'indietro per evitare sovrascrizioni
        for j in range(target, num - 1, -1):
            dp[j] = dp[j] or dp[j - num]

    return dp[target]

