    prev = [0] * (len(text2) + 1)

    for char1 in text1:
        curr = [0] * (len(text2) + 1)
        for j, char2 in enumerate(text2, 1):
            if char1 == char2:
                curr[j] = prev[j-1] + 1
            else:
                curr[j] = max(curr[j-1], prev[j])
        prev = curr

    return prev[-1]

