def lengthOfLIS(nums):
    # Approccio Binary Search O(n log n)
    import bisect

    tails = []  # tails[i] = coda pi√π piccola per LIS di lunghezza i+1

    for num in nums:
        # Trova posizione dove inserire num
        pos = bisect.bisect_left(tails, num)

        if pos == len(tails):
            tails.append(num)  # Estendi LIS
        else:
            tails[pos] = num   # Sostituisci per coda pi√π piccola

    return len(tails)

