def longestPalindromeSubseq(s):
    n = len(s)
    # dp[i][j] = LPS nella sottostringa s[i:j+1]
    dp = [[0] * n for _ in range(n)]

    # Ogni carattere singolo √® palindromo di lunghezza 1
    for i in range(n):
        dp[i][i] = 1

    # Riempi per lunghezza crescente
    for length in range(2, n + 1):
        for i in range(n - length + 1):
            j = i + length - 1

            if s[i] == s[j]:
                dp[i][j] = dp[i+1][j-1] + 2
            else:
                dp[i][j] = max(dp[i+1][j], dp[i][j-1])

    return dp[0][n-1]

