def minDistance(word1, word2):
    m, n = len(word1), len(word2)

    # dp[i][j] = edit distance tra word1[0:i] e word2[0:j]
    dp = [[0] * (n + 1) for _ in range(m + 1)]

    # Casi base
    for i in range(m + 1):
        dp[i][0] = i
    for j in range(n + 1):
        dp[0][j] = j

    for i in range(1, m + 1):
        for j in range(1, n + 1):
            if word1[i-1] == word2[j-1]:
                # Caratteri uguali: nessuna operazione
                dp[i][j] = dp[i-1][j-1]
            else:
                # Tre operazioni possibili
                dp[i][j] = 1 + min(
                    dp[i-1][j],     # cancella da word1
                    dp[i][j-1],     # inserisci in word1
                    dp[i-1][j-1]    # sostituisci in word1
                )

    return dp[m][n]

