def uniquePaths(m, n):
    # dp[i][j] = numero di percorsi per arrivare a (i,j)
    dp = [[1] * n for _ in range(m)]

    # Riempi la griglia
    for i in range(1, m):
        for j in range(1, n):
            dp[i][j] = dp[i-1][j] + dp[i][j-1]

    return dp[m-1][n-1]

