def minPathSum(grid):
    m, n = len(grid), len(grid[0])

    # Usa la griglia stessa per risparmiare spazio
    # Inizializza prima riga
    for j in range(1, n):
        grid[0][j] += grid[0][j-1]

    # Inizializza prima colonna
    for i in range(1, m):
        grid[i][0] += grid[i-1][0]

    # Riempi il resto
    for i in range(1, m):
        for j in range(1, n):
            grid[i][j] += min(grid[i-1][j], grid[i][j-1])

    return grid[m-1][n-1]

