def generateParenthesis(n):
    def generate(open_rem, close_rem, current):
        if open_rem == 0 and close_rem == 0:
            return [current]

        result = []
        if open_rem > 0:
            result.extend(generate(open_rem - 1, close_rem, current + '('))
        if close_rem > open_rem:
            result.extend(generate(open_rem, close_rem - 1, current + ')'))

        return result

    return generate(n, n, '')

