def matrixChainOrder(p):
    n = len(p) - 1
    memo = {}

    def solve(i, j):
        if i == j:
            return 0

        if (i, j) in memo:
            return memo[(i, j)]

        result = float('inf')
        for k in range(i, j):
            cost = solve(i, k) + solve(k+1, j) + p[i] * p[k+1] * p[j+1]
            result = min(result, cost)

        memo[(i, j)] = result
        return result

    return solve(0, n-1)

