def findTargetSumWays(nums, target):
    total = sum(nums)
    if target > total or target < -total or (target + total) % 2 == 1:
        return 0

    subset_sum = (target + total) // 2
    dp = [0] * (subset_sum + 1)
    dp[0] = 1

    for num in nums:
        for j in range(subset_sum, num - 1, -1):
            dp[j] += dp[j - num]

    return dp[subset_sum]

