def change(amount, coins):
    memo = {}

    def dp(i, remaining):
        if remaining == 0:
            return 1
        if i >= len(coins) or remaining < 0:
            return 0

        if (i, remaining) in memo:
            return memo[(i, remaining)]

        # Include current coin vs skip
        result = dp(i, remaining - coins[i]) + dp(i + 1, remaining)
        memo[(i, remaining)] = result
        return result

    return dp(0, amount)

