def rob(root):
    memo = {}

    def dp(node):
        if not node:
            return 0
        if node in memo:
            return memo[node]

        # Opzione 1: rapina questo nodo
        rob_current = node.val
        if node.left:
            rob_current += dp(node.left.left) + dp(node.left.right)
        if node.right:
            rob_current += dp(node.right.left) + dp(node.right.right)

        # Opzione 2: non rapinare questo nodo
        not_rob_current = dp(node.left) + dp(node.right)

        memo[node] = max(rob_current, not_rob_current)
        return memo[node]

    return dp(root)

def diameterOfBinaryTree(root):
    def dfs(node):
        if not node:
            return 0, 0  # (height, diameter)

        left_height, left_diameter = dfs(node.left)
        right_height, right_diameter = dfs(node.right)

        current_height = 1 + max(left_height, right_height)
        current_diameter = max(left_diameter, right_diameter, left_height + right_height)

        return current_height, current_diameter

    return dfs(root)[1]

