def findCheapestPrice(n, flights, src, dst, k):
    # Bellman-Ford modificato
    dp = [float('inf')] * n
    dp[src] = 0

    # Al massimo k fermate = k+1 edges
    for _ in range(k + 1):
        temp = dp[:]
        for u, v, price in flights:
            if dp[u] != float('inf'):
                temp[v] = min(temp[v], dp[u] + price)
        dp = temp

    return dp[dst] if dp[dst] != float('inf') else -1

