def findCheapestPrice(n, flights, src, dst, k):
    from collections import defaultdict, deque

    graph = defaultdict(list)
    for u, v, price in flights:
        graph[u].append((v, price))

    # BFS con limite di fermate
    queue = deque([(src, 0, 0)])  # (citt√†, costo, fermate)
    min_cost = defaultdict(lambda: float('inf'))

    while queue:
        city, cost, stops = queue.popleft()

        if stops > k + 1 or cost > min_cost[(city, stops)]:
            continue

        min_cost[(city, stops)] = cost

        if city == dst:
            continue

        for next_city, price in graph[city]:
            if stops < k + 1:
                queue.append((next_city, cost + price, stops + 1))

    result = float('inf')
    for stops in range(k + 2):
        result = min(result, min_cost[(dst, stops)])

    return result if result != float('inf') else -1

