def countNumbersWithUniqueDigits(n):
    if n == 0:
        return 1

    result = 1  # per il numero 0

    for length in range(1, n + 1):
        if length == 1:
            result += 9  # 1-9
        else:
            count = 9  # prima cifra (1-9)
            for i in range(1, length):
                count *= (10 - i)  # cifre rimanenti disponibili
            result += count

    return result

