def countNumbersWithUniqueDigits(n):
    if n == 0:
        return 1
    if n == 1:
        return 10

    # f(n) = f(n-1) + 9 * P(9, n-1)
    # dove P(9, n-1) = 9! / (9-n+1)!
    result = 10  # f(1)
    unique_digits = 9  # 9 * P(9, 0)

    for i in range(2, n + 1):
        unique_digits *= (10 - i + 1)
        result += unique_digits

    return result

