def tsp(dist):
    n = len(dist)
    dp = [[float('inf')] * n for _ in range(1 << n)]

    # Caso base: inizia dalla citt√† 0
    dp[1][0] = 0  # mask=1 (solo citt√† 0), posizione 0

    for mask in range(1 << n):
        for u in range(n):
            if not (mask & (1 << u)) or dp[mask][u] == float('inf'):
                continue

            for v in range(n):
                if mask & (1 << v):  # citt√† gi√† visitata
                    continue

                new_mask = mask | (1 << v)
                dp[new_mask][v] = min(dp[new_mask][v], dp[mask][u] + dist[u][v])

    # Torna alla citt√† 0
    result = float('inf')
    final_mask = (1 << n) - 1
    for i in range(1, n):
        if dp[final_mask][i] != float('inf'):
            result = min(result, dp[final_mask][i] + dist[i][0])

    return result

