def tsp(dist):
    n = len(dist)
    memo = {}

    def dp(mask, pos):
        if mask == (1 << n) - 1:
            return dist[pos][0]  # torna all'origine

        if (mask, pos) in memo:
            return memo[(mask, pos)]

        result = float('inf')
        for next_city in range(n):
            if mask & (1 << next_city):
                continue

            new_mask = mask | (1 << next_city)
            result = min(result, dist[pos][next_city] + dp(new_mask, next_city))

        memo[(mask, pos)] = result
        return result

    return dp(1, 0)  # inizia dalla citt√† 0

