def new21Game(n, k, maxPts):
    if k == 0 or n >= k + maxPts:
        return 1.0

    dp = [0.0] * (k + maxPts)

    # Casi base: se i ‚â• k e i ‚â§ n, vinci
    for i in range(k, min(n + 1, k + maxPts)):
        dp[i] = 1.0

    # Calcola backward
    for i in range(k - 1, -1, -1):
        for j in range(1, maxPts + 1):
            dp[i] += dp[i + j] / maxPts

    return dp[0]

